package com.imagine.card.card_application.infrastructure.kafka;import org.slf4j.Logger;import org.slf4j.LoggerFactory;import org.springframework.stereotype.Component;//  No-Op 구현체 (나중에 Kafka로 교체)@Componentpublic class NoOpCardApplicationEventPublisher implements CardApplicationEventPublisher  {    private static final Logger log =            LoggerFactory.getLogger(NoOpCardApplicationEventPublisher.class);    @Override    public void publishCardApplied(Long applicationId, Long userId, Long cardTypeId) {        log.info("[NoOp] CardApplication event ignored: applicationId={}, userId={}, cardTypeId={}",                applicationId, userId, cardTypeId);    }}